    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed

        
        String titulo = txtTitulo.getText();
        String autor = txtAutor.getText();
        String editora = txtEditora.getText();
        String genero = txtGenero.getText();   
        String AnoPublicaçao = txtAnoPublicacao.getText();
        String categoria = (String) boxTipo.getSelectedItem();
        int qntExemplares = Integer.parseInt(txtQntExemplares.getText());
        String resumo = txtResumo.getText();
        
        material material = new material();
        material.setTítulo(titulo);
        material.setAutor(autor);
        material.setEditora(editora);
        material.setGenero(genero);
        material.setAnoPublicacao(AnoPublicaçao);
        material.setTipo(categoria);
        material.setQntExemplares(qntExemplares);
        material.setResumo(resumo);
        
        materialDAO materialDAO = new materialDAO();
        int materialId = -1; // Inicialize a variável antes dos blocos if-else

        try {
            
            if (categoria.equals("LIVRO")) {
                
                materialId = materialDAO.cadastrarLivro(material);
            
            } else {
                
                String revista = txtRevista.getText();
                String volume = txtVolume.getText();
            
                material.setRevista(revista);
                material.setVolume(volume);

                materialId = materialDAO.cadastrarArtigo(material);
            }

        if (materialId == -1) {
            
            throw new Exception("ID do material não gerado.");
            
        }
        // Mensagem de sucesso
        JOptionPane.showMessageDialog(this, "Material e exemplares cadastrados com sucesso!");
        
        exemplarDAO exDAO = new exemplarDAO();
        exDAO.cadastroExemplar(material, materialId);
        
        // Mensagem de lembrete
        JOptionPane.showMessageDialog(this, "Por favor, não se esqueça de finalizar o cadastro dos exemplares.");

        // Limpar os campos após cadastro
        txtTitulo.setText("");
        txtAutor.setText("");
        txtEditora.setText("");
        txtGenero.setText("");
        txtAnoPublicacao.setText("");
        txtQntExemplares.setText("");
        txtRevista.setText("");
        txtVolume.setText("");
        txtResumo.setText("");

    } catch (Exception e) {
        
        JOptionPane.showMessageDialog(this, "Erro ao cadastrar material: " + e.getMessage(), "Erro", JOptionPane.ERROR_MESSAGE);
        
    }
           
    }//GEN-LAST:event_btnCadastrarActionPerformed

